UUID=$(grep xray_uuid host_vars/prod_server1.yml | awk '{print $2}' | tr -d '"') && SERVER_IP=$(grep prod_server1 inventory/prod/hosts.ini | awk -F'=' '{print $2}') && PORT=$(grep xray_listen_port group_vars/all.yml | awk '{print $2}') && SNI=$(grep -A 1 reality_server_names group_vars/all.yml | tail -n 1 | awk '{print $2}' | tr -d '"') && FLOW=$(grep xray_flow group_vars/all.yml | awk '{print $2}' | tr -d '"') && PUBLIC_KEY=$(grep reality_public_key host_vars/prod_server1.yml | awk '{print $2}' | tr -d '"') && SHORT_ID=$(grep reality_short_id host_vars/prod_server1.yml | awk '{print $2}' | tr -d '"') && echo "vless://${UUID}@${SERVER_IP}:${PORT}?security=reality&sni=${SNI}&flow=${FLOW}&publicKey=${PUBLIC_KEY}&shortId=${SHORT_ID}#prod_server1_final"
